mapper.insert:
		insert into
			blog_post
			(
			post_num
			,post_title
			,post_file
			,user_id

			
			,originalfile
			,savedfile
			
			)
		values
			(
			blog_post_seq.nextval
			,#{post_title}
			,#{post_file}
			,#{user_id}
			
			
			,#{originalfile}
			,#{savedfile}
			
			)
