mapper.insert:
 	insert into
 		blog_user
 		(
	 		user_id
 			, user_name
 			, user_email
 			, user_password
 		) values (
 			#{user_id}
 			, #{user_name}
 			, #{user_email}
 			, #{user_password}
 		)

mapper.update:
update 
	blog_user
set
	user_id = #{user_id}
	, user_name = #{user_name}
	, user_email = #{user_email}
	, user_password = #{user_password}
where 
	user_id = #{user_id}
